var convertData = function (data) {
    var res = [];
    for (var i = 0; i < data.length; i++) {
        var geoCoord = geoCoordMap[data[i].name];
        if (geoCoord) {
            res.push({
                name: data[i].name,
                value: geoCoord.concat(data[i].value),
                list: eval(data[i].list)
            });
        }
    }
    return res;
};


var map = echarts.init(document.getElementById('map'));
var zoom = 1;
var option = {
  geo: {
    map: 'china',
    label: {
        emphasis: {
            show: false
        }
    },
    zoom: 1,
    roam: true, // 开启鼠标缩放和平移漫游
    itemStyle: {
        normal: {
            areaColor: '#00ce9b',
            borderColor: '#eee'
        },
        emphasis: {
            areaColor: '#00bc8d'
        }
    }
  },
  series: [{
    name: '站点',
    type: 'effectScatter', // 散点（气泡）图
    coordinateSystem: 'geo',
    data: convertData(data),
    symbolSize: 8,
    rippleEffect: {
        brushType: 'stroke'
    },
    label: {
        normal: {
            formatter: '{b}',
            position: 'right',
            show: false
        },
        emphasis: {
            show: false
        }
    },
    itemStyle: {
        normal: {
            color: '#00ffff'
        }
    }
  }]
};

map.setOption(option);

var zoomTime = setInterval(function(){
	zoomIn(4);
}, 500);

function zoomIn(i){
	if(zoom <= i){
		zoom += 1;
    option.geo.zoom  = zoom;
    option.geo.center = [114.31, 30.52];
		map.setOption(option);
	}else{
		setTimeout(zoomTime);
	}
}

map.on('click', function (params) {
    if (params.data) {
        var x = params.event.event.zrX;
        var y = params.event.event.zrY;
        var name = params.data.name;
        var length = params.data.list.length;
        $('.pop').remove();
        var $pop = $('<div class="pop">'+ name +'<br>'+ length +'个站点</div>');
        $pop.css({left: x - 10, top: y - 81 });
        $('#map').append($pop);
    }else {
      $('.pop').remove();
    }
});
